mapper.select:
select count(*) from book
where 1=1

    and bookname like #{bookname}


    and publisher like #{publisher}

select * from book
where 1=1

    and bookid = #{bookid}


    and bookname = #{bookname}


    and publisher = #{publisher}


    and price = #{price}

mapper.update:
update book set bookid = bookid

    ,bookname = #{setbook.bookname}


