mapper.update:
update book set
      bookname  = #{setbook.bookname}
    , publisher = #{setbook.publisher}
    , year      = #{setbook.year}
    , price     = #{setbook.price}
    , use_yn    = #{setbook.use_yn}
    , authid    = #{setbook.authid}
where 1=1

    and bookid    = #{wherebook.bookid}


    and bookname  = #{wherebook.bookname}

mapper.delete:
delete from book where 1=1

    and bookid    = #{bookid}


    and bookname  = #{bookname}

mapper.select:
select * from book where 1=1

    and bookid = #{bookid}


    and bookname  = #{bookname}


    and publisher  = #{publisher}


    and year  = #{year}


    and price  = #{price}


    and dtm  = #{dtm}


    and use_yn  = #{use_yn}


    and authid  = #{authid}
