mapper.select:
select userid, name, email, mobile, retireYN, userno 
  from TB_USER
 WHERE retireYN = 0
   AND userid = #{userid}
   AND passwd = #{passwd}

SELECT count(*)
FROM TB_USER
where userid = #{userid}

mapper.update:
UPDATE TB_USER
email = #{setValue.email } , passwd = #{setValue.passwd } , name = #{setValue.name } , mobile = #{setValue.mobile } , retireYN = #{setValue.retireYN } , UpdateUID = #{setValue.UpdateUID} , UpdateDT = #{setValue.UpdateDT } ,
WHERE 1 = 1
AND userno = #{whereValue.userno}
AND userid = #{whereValue.userid}

	        UPDATE TB_USER 
	           SET passwd = #{newPasswd}            
	         WHERE userid = #{userid}
               AND passwd = #{currentPasswd}

UPDATE TB_USER 
   SET retireYN=1            
WHERE userid = #{userid}
