mapper.select:

SELECT boardnm
  FROM TB_Bbs_Board
 WHERE boardcd = #{boardcd}

SELECT boardcd, boardnm, UseYN 
  FROM TB_Bbs_Board 
 WHERE boardcd = #{boardcd}

SELECT boardcd, boardnm, UseYN
FROM (
    SELECT @RNUM := @RNUM + 1 AS r, a.* 
        FROM (
            SELECT 
                boardcd, boardnm, UseYN
            FROM 
                TB_BBS_Board
            WHERE 1 = 1

                AND boardcd = #{boardcd}


                AND boardnm LIKE #{searchWord} 

            GROUP BY boardcd, boardnm, UseYN
            ORDER BY boardcd DESC
        ) a, (SELECT @RNUM:=0) b
    ) temp
WHERE r BETWEEN #{start} AND #{end}

mapper.insert:
INSERT INTO TB_Bbs_Board( boardcd , boardnm , UseYN  )
VALUES

        #{item.boardcd} , #{item.boardnm} , #{item.UseYN}

mapper.update:
UPDATE TB_Bbs_Board 

       boardnm   = #{setValue.boardnm}   , 
       UseYN     = #{setValue.UseYN}     , 
       UpdateUID = #{setValue.UpdateUID} , 
       UpdateDT  = #{setValue.UpdateDT}  , 

    WHERE 1 = 1 

      AND boardcd    = #{whereValue.boardcd}


      AND boardnm  = #{whereValue.boardnm}


      AND UseYN    = #{whereValue.UseYN}

mapper.delete:
DELETE FROM TB_Bbs_Board 
 WHERE 1 = 1 

  AND boardcd  = #{boardcd}


  AND boardnm  = #{boardnm}


  AND UseYN    = #{UseYN}

mapper.select:
SELECT count(*) FROM TB_BBS_Article 
 WHERE boardcd = #{boardcd}

    AND ( title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )

SELECT articleno, title, regdate, hit, attachfileNum, commentNum , name FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM ( SELECT a.articleno articleno , a.title , a.regdate , a.hit , count(distinct(f.attachfileno)) attachfileNum , count(distinct(c.commentno)) commentNum , m.name FROM TB_BBS_Article a LEFT OUTER JOIN TB_BBS_Attachfile f ON a.articleno = f.articleno LEFT OUTER JOIN TB_BBS_Comments c ON a.articleno = c.articleno LEFT OUTER JOIN TB_User m ON a.insertUID = m.userid WHERE a.boardcd = #{boardcd}
AND (title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )
GROUP BY a.articleno, title, a.regdate, hit, m.name ORDER BY articleno DESC ) a, (SELECT @RNUM:=0) b ) temp WHERE r BETWEEN #{start} AND #{end}

SELECT articleno, boardcd, title, content, email, hit, regdate
FROM TB_BBS_Article
WHERE articleno = #{articleno}

mapper.update:
UPDATE TB_BBS_Article  

         title     = #{setValue.title    } , 
         content   = #{setValue.content  } , 
         UseYN     = #{setValue.UseYN    } , 
         UpdateUID = #{setValue.UpdateUID} , 
         UpdateDT  = #{setValue.UpdateDT } , 

 WHERE  1 = 1 

      AND boardcd    = #{whereValue.boardcd}


      AND articleno  = #{whereValue.articleno}


      AND title      = #{whereValue.title}


      AND email      = #{whereValue.email}


      AND UseYN      = #{whereValue.UseYN}

mapper.delete:
DELETE FROM TB_BBS_Article 
 WHERE 1 = 1 

  AND articleno = #{articleno}


  AND boardcd  = #{boardcd}


  AND email    = #{email}


  AND UseYN    = #{UseYN}

mapper.update:
UPDATE TB_BBS_Article 
   SET hit=hit+1 
 WHERE articleno=#{articleno}

mapper.select:
SELECT articleno, title FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM (SELECT articleno, title FROM TB_BBS_Article WHERE boardcd = #{boardcd} AND articleno > #{articleno}
AND (title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )
ORDER BY articleno ASC ) a, (SELECT @RNUM:=0) b ) temp WHERE r = 1

SELECT articleno, title FROM ( SELECT @RNUM := @RNUM + 1 AS r, a.* FROM (SELECT articleno, title FROM TB_BBS_Article WHERE boardcd = #{boardcd} AND articleno < #{articleno}
AND (title LIKE CONCAT('%', #{searchWord}, '%') OR content LIKE CONCAT('%', #{searchWord}, '%') )
ORDER BY articleno DESC ) a, (SELECT @RNUM:=0) b ) temp WHERE r = 1

SELECT attachfileno, filename, filetype, filesize, articleno
  FROM TB_BBS_Attachfile
 WHERE attachfileno = #{attachFileNo}

